function between(point, floor, ceiling ) {
	var ret = false;
	//console.log("the value: " + testVar);
	//console.log("the floor: " + floor);
	//console.log("the ceiling: " + ceiling);
	if (point >= floor && point <= ceiling) {
		ret = true;
	}
	return ret;
} 

exports.between = between;



